import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom';
import xp from "xp.png"
// import Secone from './Secone';
// import Home from '../pages/Home';
import Switching from './Switching';
import Vision from './Vision';


function Navmax() {
    const [isopen, setOpen] = useState(false);
    const Openmenu = () => {
        setOpen(!isopen);
    }
    return (
        <div className='Navmax_container'>
            <div className='Navmax'>
                <div className='burger' onClick={Openmenu}>
                    <div className='linee'></div>
                    <div className='linee'></div>
                    <div className='linee'></div>
                </div>
                <div className='logo_container'><img className='logo' src={xp} alt="logo" /></div>
                {/* <div> */}
                <button className='contact'>Contact</button>
                {/* </div> */}
            </div>
            {isopen && (
                <div className='burger_menu'>
                    <div><Link to="/">AbOut</Link></div>
                    <div><Link to="/">VisIon</Link></div>
                    <div><Link to="/">GoAls</Link></div>
                    <div><Link to="/">Founder's</Link></div>
                </div>
            )}
        </div >
    )
}
const Modal = ({ onClose }) => {
    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={onClose}>&times;</span>
          <h2>Contact Us</h2>
          <p>This is a modal window. You can put your contact form or any other content here.</p>
        </div>
      </div>
    );
  };

function Navmin() {
    const [isScrolled, setIsScrolled] = useState(false);
    const [showModal, setShowModal] = useState(false); // State for controlling modal visibility

    let x = 0;
    useEffect(() => {
        const handleScroll = () => {
            console.log(window.scrollY)
            let i = window.scrollY;
            if (i > x || i > 0) {
                x = i;
                console.log(`x ${x}`);
                setIsScrolled(true);
            }
            else if(x > i)
            {
                setIsScrolled(false)
            }
            else {
                setIsScrolled(false);
            }
        };

        window.addEventListener('scroll', handleScroll);

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);
    const handleContact = ()=>{
        document.body.style
        setShowModal(true)
    }

  const closeModal = () => {
    // Close the modal
    setShowModal(false);
  };
    return (
        <div>
        <div className={`Navmin ${isScrolled ? 'shrunk' : ''}`}>
            <div className='logo_container'><img src={xp} alt="logo" /></div>
            <div className='links'>
                <Link to="/">AbOut</Link>
                <Link to="/">VisIon</Link>
                <Link to="/">GoAls</Link>
                <Link to="/">Founder's</Link>
                <button className='contact' onClick={handleContact}>Contact</button>
            </div>
        </div>
        {showModal && <Modal onClose={closeModal} />}
        </div>
    )
}

function Main() {
    const [isWideScreen, setIsWideScreen] = useState(window.matchMedia("(min-width: 500px)").matches);

    useEffect(() => {
        const handleResize = () => {
            setIsWideScreen(window.matchMedia("(min-width: 500px)").matches);
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);
    return (
        <div>
            {isWideScreen ? <Navmin /> : <Navmax />}
            {/* <Secone/> */}
            <Switching/>
        </div>
    )
}

export default Main
