import React, { useEffect, useRef } from 'react';
import ash from "ashh.png";
import hexa from "hexa.png";
import aym from "aym.png";

function Founder() {
    const cardsContainerRef = useRef(null);
    const overlayRef = useRef(null);

    useEffect(() => {
        const applyOverlayMask = (e) => {
            const cardsContainer = cardsContainerRef.current;
            const overlayEl = overlayRef.current;
            if (!cardsContainer || !overlayEl) return;

            const x = e.pageX - cardsContainer.offsetLeft;
            const y = e.pageY - cardsContainer.offsetTop;

            overlayEl.style = `--opacity: 1; --x: ${x}px; --y:${y}px;`;
        };

        if (cardsContainerRef.current) {
            document.body.addEventListener("pointermove", applyOverlayMask);
        }

        return () => {
            document.body.removeEventListener("pointermove", applyOverlayMask);
        };
    }, []);

    useEffect(() => {
        const cards = Array.from(cardsContainerRef.current.querySelectorAll(".card"));

        const createOverlayCta = (overlayCard, ctaEl) => {
            const overlayCta = document.createElement("div");
            overlayCta.classList.add("cta");
            overlayCta.textContent = ctaEl.textContent;
            overlayCta.setAttribute("aria-hidden", true);
            overlayCard.append(overlayCta);
        };

        const observer = new ResizeObserver((entries) => {
            entries.forEach((entry) => {
                const cardIndex = cards.indexOf(entry.target);
                let width = entry.borderBoxSize[0].inlineSize;
                let height = entry.borderBoxSize[0].blockSize;

                if (cardIndex >= 0) {
                    overlayRef.current.children[cardIndex].style.width = `${width}px`;
                    overlayRef.current.children[cardIndex].style.height = `${height}px`;
                }
            });
        });

        const initOverlayCard = (cardEl) => {
            const overlayCard = document.createElement("div");
            overlayCard.classList.add("card");
            createOverlayCta(overlayCard, cardEl.lastElementChild);
            overlayRef.current.append(overlayCard);
            observer.observe(cardEl);
        };

        cards.forEach(initOverlayCard);

        return () => {
            observer.disconnect();
        };
    }, []);

    return (
        <div className='found_container'>
            <h1 style={{fontWeight: 600, fontSize: '2.25em', marginBottom: '0.75em', textAlign: 'center', color: '#eceff1'}}>Founder'</h1>
            <div className='fd_cards' ref={cardsContainerRef}>
                <div className='card' style={{'--hue': '165', '--saturation': '82.26%', '--lightness': '51.37%', flex: '1 1 14rem', padding: '1.5em 2em', display: 'grid', gridTemplateRows: 'auto auto auto 1fr', alignItems: 'start', gap: '1.25em', color: '#eceff1', backgroundColor: '#2b2b2b', border: '1px solid #eceff133', borderRadius: '15px'}}>
                    <img src={ash} alt='ash' className='fd_pic' />
                    <h3 style={{fontSize: '1.05em', fontWeight: '600'}}>ACHRAF ABDELADIM</h3>
                    <h5>CTO-FOUNDER</h5>
                    <p>
                        My name is Ashraf Abdeladim, my mission at Xphenix is ​​to create a website where we can sell our products
                        Xphenix to me is more than just a online branding for me is a future that we must build it together
                    </p>
                </div>
                {/* Other cards */}
                <div className="overlay cards__inner" ref={overlayRef} style={{position: 'absolute', inset: '0', pointerEvents: 'none', userSelect: 'none', opacity: 'var(--opacity, 0)', WebkitMask: 'radial-gradient(25rem 25rem at var(--x) var(--y), #000 1%, transparent 50%)', mask: 'radial-gradient(25rem 25rem at var(--x) var(--y), #000 1%, transparent 50%)', transition: '400ms mask ease', willChange: 'mask'}}></div>
            </div>
        </div>
    );
}

export default Founder;
