import React, { useEffect } from 'react'
import ash from "ashh.png"
import hexa from "hexa.png"
import aym from "aym.png"

function Founder() {

    useEffect(() => {
        console.clear();

        const cardsContainer = document.querySelector(".cards");
        const cardsContainerInner = document.querySelector(".cards__inner");
        const cards = Array.from(document.querySelectorAll(".card"));
        const overlay = document.querySelector(".overlay");

        const applyOverlayMask = (e) => {
            const overlayEl = e.currentTarget;
            // const x = e.pageX - cardsContainer.offsetLeft;
            const y = e.pageY - cardsContainer.offsetTop;

            // overlayEl.style = `--opacity: 1; --x: ${x}px; --y:${y}px;`;
        };

        const createOverlayCta = (overlayCard, ctaEl) => {
            const overlayCta = document.createElement("div");
            overlayCta.classList.add("cta");
            overlayCta.textContent = ctaEl.textContent;
            overlayCta.setAttribute("aria-hidden", true);
            overlayCard.append(overlayCta);
        };

        const observer = new ResizeObserver((entries) => {
            entries.forEach((entry) => {
                const cardIndex = cards.indexOf(entry.target);
                let width = entry.borderBoxSize[0].inlineSize;
                let height = entry.borderBoxSize[0].blockSize;

                if (cardIndex >= 0) {
                    overlay.children[cardIndex].style.width = `${width}px`;
                    overlay.children[cardIndex].style.height = `${height}px`;
                }
            });
        });

        const initOverlayCard = (cardEl) => {
            const overlayCard = document.createElement("div");
            overlayCard.classList.add("card");
            createOverlayCta(overlayCard, cardEl.lastElementChild);
            overlay.append(overlayCard);
            observer.observe(cardEl);
        };

        cards.forEach(initOverlayCard);
        document.body.addEventListener("pointermove", applyOverlayMask);

    })
    return (
        <div className='found_container'>
            <h1>Founder'</h1>
            <div className='fd_cards'>
                <div className='card'>
                    <img src={ash} alt='ash' className='fd_pic' />
                    <h3>ACHRAF ABDELADIM</h3>
                    <h5>CTO-FOUNDER</h5>
                    <p>
                        My name is Ashraf Abdeladim, my mission at Xphenix is ​​to create a website where we can sell our products
                        Xphenix to me is more than just a online branding for me is a future that we must build it togheter
                    </p>
                </div>
                <div className='card'>
                    <img src={aym} alt='aym' className='fd_pic' />
                    <h3>AYMAN OUZALI</h3>
                    <h5>COO-FOUNDER</h5>
                    <p>
                        My name is aymane ouzali 23 years old, I have a bachelor's degree in hassan universe 2 Economics and Management and I had done auto studies in graphic design and I worked freelance and I have a marketing certificate digital. my mission at X-phenix designer of our brand and everything related to digital marketing
                    </p>
                </div>
                <div className='card'>
                    <img src={hexa} alt='hexa' className='fd_pic' />
                    <h3>HAITAM ABDELADIM</h3>
                    <h5>CEO-FOUNDER</h5>
                    <p>
                        Haitam abdeladim 23 years old, I am in the process of obtaining my degree in Hassan 2 Economy and Management, I have completed training on Salesforce Administrator, my role on X-phenix consists of managing the project and managing the budget, production and sales
                    </p>
                </div>
                <div class="overlay cards__inner"></div>
            </div>
        </div>
    )
}

export default Founder
